mapper.update:
update board_study_bak
set board_subject = #{board_subject}
, board_content = #{board_content}
, upt_date = (select SYSDATE from SYS.DUAL)
where seq = #{seq}

mapper.select:
select rownum as rnum , seq, mem_id, mem_name, board_subject, board_content, reg_date, upt_date, view_cnt from ( select rownum as rnum , seq, mem_id, mem_name, board_subject, board_content, reg_date, upt_date, view_cnt from board_study_bak order by seq desc ) where 1=1 AND rnum Between #{queryPageStart} and #{queryPageEnd}
AND mem_name like '%'|| #{serch_text} || '%'
AND board_subject like '%'|| #{serch_text} || '%'
AND board_content like '%'|| #{serch_text} || '%' OR board_subject like '%'|| #{serch_text} || '%'
AND reg_date BETWEEN #{sdate} AND #{edate}
