mapper.select:
SELECT * FROM ( SELECT SB.SEQ, EM.EMP_NAME AS EMP_NUM, SB.SIGN_SUBJECT, SB.SIGN_CONTENT, SB.REG_DATE, SB.SIGN_DATE, (SELECT EMP_NAME FROM B_EMPLOYEE WHERE EMP_NUM = SB.SIGN_PERSON) AS SIGN_PERSON, SS.STATE_NAME AS SIGN_STATE FROM B_SIGNBOARD SB, B_EMPLOYEE EM, B_SIGN_STATE SS WHERE 1=1 AND SB.EMP_NUM = EM.EMP_NUM(+) AND SS.STATE_NUM = SB.SIGN_STATE AND SB.EMP_NUM =#{empNum}
AND reg_date BETWEEN #{sdate} AND #{edate}
union ALL
SELECT SB.SEQ, EM.EMP_NAME AS EMP_NUM, SB.SIGN_SUBJECT, SB.SIGN_CONTENT, SB.REG_DATE, SB.SIGN_DATE, (SELECT EMP_NAME FROM B_EMPLOYEE WHERE EMP_NUM = SB.SIGN_PERSON) AS SIGN_PERSON, SS.STATE_NAME AS SIGN_STATE FROM B_SIGNBOARD SB, B_EMPLOYEE EM, B_SIGN_STATE SS, (SELECT * FROM B_SIGN WHERE EMP_NUM = #{empNum} ) sign WHERE 1=1 AND SB.EMP_NUM = EM.EMP_NUM(+) AND SS.STATE_NUM = SB.SIGN_STATE AND SB.SEQ = sign.BOARD_NUM AND sign.SIGN_REQUEST = 'Y' AND SB.EMP_NUM not in(#{empNum})
AND reg_date BETWEEN #{sdate} AND #{edate}
ORDER BY SEQ DESC ) WHERE 1=1
AND EMP_NUM LIKE '%'||#{search_text}||'%'
AND SIGN_SUBJECT LIKE '%'||#{search_text}||'%'
AND SIGN_PERSON LIKE '%'||#{search_text}||'%'
AND SIGN_STATE = #{sign_box}

choose.when:
UPDATE B_SIGNBOARD
SET SIGN_STATE = #{signState}
WHERE SEQ = #{seq}

choose.otherwise:
UPDATE B_SIGNBOARD
SET SIGN_STATE = #{signState},
	SIGN_DATE = SYSDATE,
	SIGN_PERSON = #{empNum}
WHERE SEQ = #{seq}

mapper.update:

UPDATE B_SIGN
SET SIGN_YN = #{signState}
WHERE EMP_NUM = #{empNum}
AND BOARD_NUM= #{boardNum}

mapper.select:
SELECT COUNT(*) FROM B_SIGN_LIST
WHERE EMP_NUM = #{empNum}
AND SING_FLAG = (SELECT MAX(SING_FLAG) FROM B_SIGN_LIST)
